mapper.update:
UPDATE diary

	user_category_no = #{userCategoryNo},
	diary_title = #{diaryTitle},
	diary_detail = #{diaryDetail}	

WHERE diary_no = #{diaryNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT d.diary_no ,d.user_no, d.user_category_no, d.diary_title, d.diary_detail, d.diary_date, u.user_category_name, u.user_category_color FROM diary d, user_category u
d.user_category_no=u.user_category_no and d.user_no=#{userNo}
and (d.diary_title like '%'||#{search.searchKeyword}||'%' or d.diary_detail like '%'||#{search.searchKeyword}||'%')
ORDER BY diary_no) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT d.diary_no , d.user_no, d.user_category_no, d.diary_title, d.diary_detail, d.diary_date, u.user_category_name, u.user_category_color FROM diary d, user_category u
d.user_category_no=u.user_category_no and d.user_no=#{userNo}
and (d.diary_title like '%'||#{search.searchKeyword}||'%' or d.diary_detail like '%'||#{search.searchKeyword}||'%')
) countTable
