mapper.select:
SELECT *
FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	FROM		(	SELECT faq_no, user_center_category, faq_title, faq_detail, faq_date
						FROM faq
						
							
							
								faq_title=#{searchKeyword}
							
							
								faq_detail=#{searchKeyword} 
							
							
						
						ORDER BY faq_no ) inner_table
				WHERE ROWNUM <= #{endRowNum} )
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE faq
	
	user_center_category = #{FAQCategory},
	faq_title = #{FAQTitle},
	faq_detail = #{FAQDetail}

WHERE faq_no = #{FAQNo}

mapper.select:
   	SELECT faq_no, user_center_category, faq_title, faq_detail, faq_date
   	FROM faq
	WHERE faq_no= #{value}
