mapper.select:
SELECT *
FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	FROM		(	SELECT user_no, qna_no, user_center_category, qna_title, qna_detail, qna_date, qna_answer_check
					FROM qna
					
					user_no = #{userNo}
						
							
								AND qna_title=#{search.searchKeyword}
							
							
								AND qna_detail=#{search.searchKeyword} 
							
						
					 
					 ORDER BY qna_no ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT  qna_no, user_center_category, qna_title, qna_detail, qna_date
							FROM qna
							
								user_no=#{userNo}
							
							) countTable

mapper.update:
	UPDATE qna
  	
  		user_center_category = #{QNACategory},
  		qna_title = #{QNATitle},
  		qna_detail = #{QNADetail}
  	
  	WHERE qna_no=#{QNANo}

mapper.select:
   	SELECT qna_no, user_center_category , qna_title , qna_detail, qna_date, qna_answer_check, qna_answer_detail, qna_answer_date
   	FROM qna
	WHERE qna_no= #{value}

mapper.update:
UPDATE qna
qna_answer_check = 1, qna_answer_detail = #{QNAAnswerDetail}, qna_answer_date = SYSDATE
