mapper.select:
SELECT *
FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	FROM		(	SELECT qna_no, user_center_category, qna_title, qna_detail, qna_date, qna_answer_check
					FROM qna
					
						user_no = #{userNo}
							
								
									qna_title=#{search.searchKeyword}
								
								
									qna_detail=#{search.searchKeyword} 
								
							
						
							ORDER BY qna_no ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT  qna_no, user_center_category, qna_title, qna_detail, qna_date
							FROM qna
							
								user_no=#{userNo}
							
							) countTable

SELECT *
FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	FROM		(	SELECT qna_no, user_center_category, qna_title, qna_detail, qna_date, qna_answer_check
					FROM qna
							
								
								
									qna_title=#{search.searchKeyword}
								
								
									qna_detail=#{search.searchKeyword} 
								
								
							
							ORDER BY qna_no ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

mapper.update:
	UPDATE qna
  	
  		user_center_category = #{QNACategory},
  		qna_title = #{QNATitle},
  		qna_detail = #{QNADetail}
  	
  	WHERE qna_no=#{QNANo}

UPDATE qna
qna_answer_check = 1, qna_answer_detail = #{QNAAnswerDetail}, qna_answer_date = SYSDATE
WHERE qna_no=#{QNANo}

UPDATE qna
qna_answer_check = 1, qna_answer_detail = #{QNAAnswerDetail}, qna_answer_date = SYSDATE
WHERE qna_no=#{QNANo}
