mapper.select:
SELECT *
FROM schedule
WHERE user_no = #{userNo}
AND (schedule_start_date BETWEEN #{startDate} AND #{endDate}
OR schedule_end_date BETWEEN #{startDate} AND #{endDate})

mapper.update:
UPDATE schedule
user_category_no = #{userCategoryNo}, schedule_title = #{scheduleTitle}, schedule_detail = #{scheduleDetail}, schedule_location = #{scheduleLocation}, schedule_start_date = #{scheduleStartDate}, schedule_end_date = #{scheduleEndDate}, going_duration = #{goingDuration}, coming_duration = #{comingDuration}, schedule_dday = #{isScheduleDDay}, mark_dday = #{markDDay}, schedule_important = #{isScheduleImportant}, schedule_alarm_time = #{scheduleAlarmTime}
WHERE schedule_no = #{scheduleNo}
