SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT scrap_no , scrap_category, scrap_title, scrap_date, scrap_category_name, scrap_type FROM scrap s, scrap_category c
user_no=#{userNo} AND s.scrap_category = c.scrap_category_no
ORDER BY scrap_no DESC) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT scrap_no , scrap_category, scrap_title, scrap_date, scrap_category_name, scrap_type FROM scrap s, scrap_category c
user_no=#{userNo} AND s.scrap_category = c.scrap_category_no
) countTable
