mapper.select:
SELECT user_no, user_id, password, email, user_name, birthday, role, withdraw_check
FROM users 
WHERE user_no = #{value}

SELECT user_no, user_id, password, role, userName
FROM users 
WHERE user_id = #{value}

SELECT user_no, user_id, password, role, email
FROM users
WHERE email = #{value}

SELECT user_id
FROM users
WHERE user_name = #{userName} AND email = #{email}

SELECT password
FROM users
WHERE user_id = #{userId} AND email = #{email}

mapper.update:
UPDATE users
user_name=#{userName:VARCHAR}, password=#{password:VARCHAR}, birthday=#{birthday:DATE}, role=#{role:NUMERIC}, email=#{email}
WHERE user_no=#{userNo}

UPDATE users
WITHDRAW_CHECK=#{withdrawCheck}, WITHDRAW_DATE=SYSDATE
WHERE user_no=#{userNo}

mapper.select:
  	SELECT *
  	FROM ( SELECT inner_table.* , ROWNUM AS row_seq
  		   FROM ( SELECT user_no, user_id , user_name , email, withdraw_check, withdraw_date, sns_name
			      FROM users
					
						
							
									user_id = #{searchKeyword}
							
							
									user_name = #{searchKeyword}
							
						
					
			      ORDER BY user_no ) inner_table
		   WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  SELECT COUNT(*)
  FROM(	SELECT user_no, user_id , user_name , email, withdraw_check, withdraw_date, sns_name
		FROM users
		
			
				
		 			user_id = #{searchKeyword}
				
				
